import React, { Component } from "react";

import "./App.css";

const formatNumber = (number) => new Intl.NumberFormat("en", { minimumFractionDigits: 2 }).format(number);
class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      todos: []
    };  
  }
  async componentDidMount() {
    try {
      const res = await fetch('./api/branch1.json');
      const todos = await res.json();
      this.setState({
        todos: todos
      });
      console.log('state',this.state.todos)
    } 
    catch (e) {
      console.log(e);
    }
  }  
  renderProducts() {
    return this.state.todos.map(product => {
      return (
        <tr key={product.id}>
          <td>{product.name}</td>
          <td>{product.unitPrice}</td>
        </tr>
      );
    })
  }

  render() {
    return (
      <div class="product-list">
        <label>Search Products</label>
        <input type="text" />

        <table>
        <thead>
          <tr>
            <th>Product</th>
            <th>Revenue</th>
          </tr>
        </thead>
        <tbody>
          {this.renderProducts()}
        </tbody>
        <tfoot>
          <tr>
            <td>Total</td>
            <td></td>
          </tr>
        </tfoot>
      </table>

    </div>

  );
  }
}

export default App;
