import React, { Component } from "react";

import "./App.css";

const formatNumber = (number) => new Intl.NumberFormat("en", { minimumFractionDigits: 2 }).format(number);
class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      todos: []
    };  
  }
  async componentDidMount() {
    try {
      const res = await fetch('public/api/branch1.json');
      const todos = await res.json();
      this.setState({
        todos
      });
    } 
    catch (e) {
      console.log(e);
    }
  }  
  render() {
    return (
      <div class="product-list">
        <label>Search Products</label>
        <input type="text" />

        <table>
        <thead>
          <tr>
            <th>Product</th>
            <th>Revenue</th>
          </tr>
        </thead>
        <tbody>
        <td>
        {item.name}</td>
        <td>{item.unitPrice}</td>
        </tbody>
        <tfoot>
          <tr>
            <td>Total</td>
            <td></td>
          </tr>
        </tfoot>
      </table>
      {this.state.todos.map(item => (
                <div key={item.id}>
                  <h1>{item.name}</h1>
                  <span>{item.unitPrice}</span>
                </div>
              ))}
    </div>

  );
  }
}

export default App;
