import React, { Component } from "react";
import axios from 'axios';
import "./App.css";

const formatNumber = (number) => new Intl.NumberFormat("en", { minimumFractionDigits: 2 }).format(number);
class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      products: [],
      search:null
    };  
  }

  searchSpace=(event)=>{
    let keyword = event.target.value;
    this.setState({search:keyword})
  }

  componentDidMount(){

    Promise.all([
      fetch('api/branch1.json'),
      fetch('api/branch2.json'),
      fetch('api/branch3.json')
    ]).then(function (responses) {
      // Get a JSON object from each of the responses
      return Promise.all(responses.map(function (response) {
        return response.json();
      }));
    }).then(data => {
      // Log the data to the console
      // You would do something with both sets of data here
        this.setState({ products: data });
        console.log('data',data);
        console.log('products',this.state.products);
    }).catch(function (error) {
      // if there's an error, log it
      console.log('error',error);
    });


  }

  render() {
    const products = this.state
    const ProductTotal = products.products.filter((data)=>{
              if(this.state.search == null)
                return data
              else if(data.name.toLowerCase().includes(this.state.search.toLowerCase())) {
                return data
        }
        }).reduce((totalProducts, product) => totalProducts + product.unitPrice, 0);
    console.log('total',ProductTotal)

    return (
      <div class="product-list">
        <label>Search Products</label>
        <input type="text" onChange={(e)=>this.searchSpace(e)}/>

        <table>
        <thead>
          <tr>
            <th>Product</th>
            <th>Revenue</th>
          </tr>
        </thead>
        <tbody>
         {
            products.Object.products.filter((data)=>{
              if(this.state.search == null)
                return data
              else if(data.name.toLowerCase().includes(this.state.search.toLowerCase())) {
                return data
        }
        }).map(function (element) {
               return <tr>
                 <td>{element.name}</td>
                 <td>{element.unitPrice}</td>
               </tr>;
              })
          }
        </tbody>
        <tfoot>
          <tr>
            <td>Total</td>
            <td>{ProductTotal}</td>
          </tr>
        </tfoot>
      </table>

    </div>

  );
  }
}

export default App;

