import React, { Component } from "react";
import axios from 'axios';
import "./App.css";

const formatNumber = (number) => new Intl.NumberFormat("en", { minimumFractionDigits: 2 }).format(number);
class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      products: [],
      branchInfo1: [], 
      branchInfo2: [],
      branchInfo3: [],
      search:null
    };  
  }

  searchSpace=(event)=>{
    let keyword = event.target.value;
    this.setState({search:keyword})
  }

  componentDidMount = () => {
    axios.get("api/branch1.json").then(json => {
        this.setState({ products: json.data.products });
        console.log('state',this.state.products)
    });
  }

   componentDidMount(){
      Promise.all([
          fetch('api/branch1.json'),
          fetch('api/branch2.json'),
          fetch('api/branch3.json')
      ])
      .then(([res1, res2, res3]) => Promise.all([res1.json(), res2.json(), res3.json()]))
      .then(([data1, data2, data3]) => this.setState({
          branchInfo1: data1, 
          branchInfo2: data2,
          branchInfo3: data3
      }));
      console.log('branchInfo1',this.state.branchInfo1)
      console.log('branchInfo2',this.state.branchInfo2)
      console.log('branchInfo3',this.state.branchInfo3)
  }

  // async componentDidMount() {
  //   try {
  //     const res = await fetch('./api/branch1.json');
  //     const todos = await res.json();
  //     const obj = Object.entries(todos).map(([key, value]) => ({key, value}))

  //     this.setState({
  //       todos:obj
  //     });

  //     console.log(obj)
  //     console.log('state',this.state.todos)
  //   } 
  //   catch (e) {
  //     console.log(e);
  //   }
  // }  

  // const champObject = { id: "jarvaniv", key: "59", name: "Jarvan IV", sprite: { url: "http://ddragon.leagueoflegends.com/cdn/8.11.1/img/sprite/champion1.png", x: 96, y: 48 }, stats: { hp: 571.2, hpperlevel: 90, mp: 302.2, mpperlevel: 40, movespeed: 340 }, tags: ["Tank", "Fighter"], title: "the Exemplar of Demacia" }


  // renderProducts() {
  //   return this.state.todos.products.map(function(product,key) {
  //     return (
  //       <tr key={key}>
  //         <td>{product.name}</td>
  //         <td>{product.unitPrice}</td>
  //       </tr>
  //     );
  //   })

  // }

  render() {
    const products = this.state
    const ProductTotal = products.products.filter((data)=>{
              if(this.state.search == null)
                return data
              else if(data.name.toLowerCase().includes(this.state.search.toLowerCase())) {
                return data
        }
        }).reduce((totalProducts, product) => totalProducts + product.unitPrice, 0);
    console.log('total',ProductTotal)

    return (
      <div class="product-list">
        <label>Search Products</label>
        <input type="text" onChange={(e)=>this.searchSpace(e)}/>

        <table>
        <thead>
          <tr>
            <th>Product</th>
            <th>Revenue</th>
          </tr>
        </thead>
        <tbody>
         {
            products.products.filter((data)=>{
              if(this.state.search == null)
                return data
              else if(data.name.toLowerCase().includes(this.state.search.toLowerCase())) {
                return data
        }
        }).map(function (element) {
               return <tr>
                 <td>{element.name}</td>
                 <td>{element.unitPrice}</td>
               </tr>;
              })
          }
        </tbody>
        <tfoot>
          <tr>
            <td>Total</td>
            <td>{ProductTotal}</td>
          </tr>
        </tfoot>
      </table>

    </div>

  );
  }
}

export default App;

